import React, { useState } from "react";
import axios from "axios";
import "./cha"

const Chatbot = () => {
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [question, setQuestion] = useState("");
  const [message, setMessage] = useState("");
  const [otpSent, setOtpSent] = useState(false);
  const [verified, setVerified] = useState(false);

  const handleSendOtp = async () => {
    if (!email) {
      setMessage("Please enter your email.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/send-otp", { email });
      setMessage(response.data.message);
      setOtpSent(true);
    } catch (error) {
      setMessage(error.response?.data?.message || "Failed to send OTP. Please try again.");
    }
  };

  const handleVerifyOtp = async () => {
    if (!otp) {
      setMessage("Please enter the OTP.");
      return;
    }

    try {
      setVerified(true);
      setMessage("OTP verified! You can now ask questions.");
    } catch (error) {
      setMessage("Invalid OTP. Please try again.");
    }
  };

  const handleAskQuestion = async () => {
    if (!verified) {
      setMessage("Please verify OTP before asking a question.");
      return;
    }

    if (!question) {
      setMessage("Please enter a question.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/chatbot", {
        email,
        otp,
        question,
      });
      setMessage(response.data.message);
    } catch (error) {
      setMessage(error.response?.data?.message || "Error occurred. Please try again.");
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>Chatbot with OTP Verification</h2>

      {!otpSent ? (
        <>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={{ padding: "10px", margin: "10px", width: "300px" }}
          />
          <button onClick={handleSendOtp} style={{ padding: "10px 20px", cursor: "pointer" }}>
            Send OTP
          </button>
        </>
      ) : !verified ? (
        <>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            style={{ padding: "10px", margin: "10px", width: "300px" }}
          />
          <button onClick={handleVerifyOtp} style={{ padding: "10px 20px", cursor: "pointer" }}>
            Verify OTP
          </button>
        </>
      ) : (
        <>
          <textarea
            placeholder="Ask your programming question..."
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            style={{ padding: "10px", margin: "10px", width: "300px", height: "100px" }}
          />
          <button onClick={handleAskQuestion} style={{ padding: "10px 20px", cursor: "pointer" }}>
            Ask Question
          </button>
        </>
      )}

      {message && <p style={{ color: "red", marginTop: "10px" }}>{message}</p>}
    </div>
  );
};

export default Chatbot;
