import * as api from "../api"
export const fetchallusers=()=> async(dispatch)=>{
    try {
        const {data}=await api.getallusers();
        dispatch({type:"FETCH_USERS",payload:data});
    } catch (error) {
        console.log(error)
    }
}

export const updateprofile=(id,updatedata)=>async(dispatch)=>{
    try {
        const {data}=await api.updateprofile(id,updatedata);
        dispatch({type:"UPDATE_CURRENT_USER",payload:data});
    } catch (error) {
        console.log(error)
    }
}

export const searchUser = async (email) => {
  try {
    const response = await api.searchUser()
    return response.data; // Return the user object
  } catch (error) {
    throw error.response?.data?.message || "Error searching user.";
  }
};

// Action to transfer points
export const transferPoints = async (senderId, receiverEmail, points) => {
  try {
    const response = await api.transferPoints()
    return response.data; // Return success message
  } catch (error) {
    throw error.response?.data?.message || "Error transferring points.";
  }
};
