import { generateOtpApi, getChatbotResponseApi } from "../api/index";

export const generateOtp = (email) => async (dispatch) => {
  try {
    dispatch({ type: "OTP_REQUEST" });
    await generateOtpApi(email);
    dispatch({ type: "OTP_SUCCESS" });
  } catch (error) {
    dispatch({ type: "OTP_FAIL", payload: error.response.data.message });
  }
};

export const getChatbotResponse = (data) => async (dispatch) => {
  try {
    dispatch({ type: "CHATBOT_REQUEST" });
    const response = await getChatbotResponseApi(data);
    dispatch({ type: "CHATBOT_SUCCESS", payload: response.data.answer });
  } catch (error) {
    dispatch({ type: "CHATBOT_FAIL", payload: error.response.data.message });
  }
};
