import React, { useState } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const ChatBot = () => {
  const [question, setQuestion] = useState("");
  const [otp, setOtp] = useState("");
  const [response, setResponse] = useState("");
  const [otpSent, setOtpSent] = useState(false);

  const sendOtp = async () => {
    try {
      const res = await axios.post("http://localhost:5000/send-otp");
      if (res.data.success) {
        toast.success("OTP sent successfully!");
        setOtpSent(true);
      }
    } catch (err) {
      toast.error("Failed to send OTP!");
    }
  };

  const askQuestion = async () => {
    if (!otpSent) {
      toast.warn("Please request and enter OTP first.");
      return;
    }
    try {
      const res = await axios.post("http://localhost:5000/ask", { question, otp });
      setResponse(res.data.message);
    } catch (err) {
      toast.error(err.response?.data?.message || "Something went wrong!");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Programming Chatbot</h1>
      <input
        type="text"
        placeholder="Enter your question"
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
        style={{ width: "100%", marginBottom: "10px" }}
      />
      <button onClick={sendOtp} disabled={otpSent}>
        {otpSent ? "OTP Sent" : "Request OTP"}
      </button>
      <br />
      <input
        type="text"
        placeholder="Enter OTP"
        value={otp}
        onChange={(e) => setOtp(e.target.value)}
        style={{ width: "100%", marginTop: "10px" }}
      />
      <button onClick={askQuestion}>Ask Question</button>
      <div style={{ marginTop: "20px", background: "#f4f4f4", padding: "10px" }}>
        <h3>Response:</h3>
        <p>{response}</p>
      </div>
      <ToastContainer />
    </div>
  );
};

export default ChatBot;
