import React, { useState } from "react";
import axios from "axios";

const Chatbot = () => {
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [question, setQuestion] = useState("");
  const [message, setMessage] = useState("");
  const [otpSent, setOtpSent] = useState(false);
  const [verified, setVerified] = useState(false);
  const [answer, setAnswer] = useState("");

  const handleSendOtp = async () => {
    if (!email) {
      setMessage("Please enter your email.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/send-otp", { email });
      setMessage(response.data.message);
      setOtpSent(true);
    } catch (error) {
      setMessage(error.response?.data?.message || "Failed to send OTP. Please try again.");
    }
  };

  const handleVerifyOtp = async () => {
    if (!otp) {
      setMessage("Please enter the OTP.");
      return;
    }

    try {
      setVerified(true);
      setMessage("OTP verified! Generating your answer...");
      handleGetAnswer(); // Generate answer after OTP is verified
    } catch (error) {
      setMessage("Invalid OTP. Please try again.");
    }
  };

  const handleAskQuestion = async () => {
    if (!question) {
      setMessage("Please enter a question.");
      return;
    }

    // Reset states for OTP
    setOtp("");
    setVerified(false);
    setOtpSent(false);
    setAnswer("");

    // Send OTP when a question is asked
    handleSendOtp();
  };

  const handleGetAnswer = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/chatbot", {
        email,
        question,
      });
      setAnswer(response.data.answer);
      setMessage("");
    } catch (error) {
      setMessage(error.response?.data?.message || "Error occurred. Please try again.");
    }
  };

  return (
    <div className="chatbot-container">
      <h2 className="chatbot-header">Chatbot with OTP Verification</h2>

      <div className="chatbot-input-section">
        <input
          type="email"
          placeholder="Enter your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="chatbot-input"
        />
        <textarea
          placeholder="Ask your programming question..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          className="chatbot-textarea"
        />
        <button onClick={handleAskQuestion} className="chatbot-button">
          Ask Question
        </button>
      </div>

      {otpSent && !verified && (
        <div className="chatbot-otp-section">
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            className="chatbot-input"
          />
          <button onClick={handleVerifyOtp} className="chatbot-button">
            Verify OTP
          </button>
        </div>
      )}

      {message && <p className="chatbot-message">{message}</p>}

      {answer && (
        <div className="chatbot-answer">
          <h3>Answer:</h3>
          <p>{answer}</p>
        </div>
      )}
    </div>
  );
};

export default Chatbot;
