const initialState = {
    loading: false,
    chatbotAnswer: "",
    error: null,
    otpSent: false,
  };
  
  export const chatbotReducer = (state = initialState, action) => {
    switch (action.type) {
      case "OTP_REQUEST":
      case "CHATBOT_REQUEST":
        return { ...state, loading: true, error: null };
      case "OTP_SUCCESS":
        return { ...state, loading: false, otpSent: true };
      case "CHATBOT_SUCCESS":
        return { ...state, loading: false, chatbotAnswer: action.payload };
      case "OTP_FAIL":
      case "CHATBOT_FAIL":
        return { ...state, loading: false, error: action.payload };
      default:
        return state;
    }
  };
