      <input
        className="otp-input"
        type="email"
        placeholder="Enter your email"
        value={emailOrMobile}
        onChange={(e) => setEmailOrMobile(e.target.value)}
      />
    ) : (
      <input
        className="otp-input"
        type="text"
        placeholder="Enter your mobile number"
        value={emailOrMobile}
        onChange={(e) => setEmailOrMobile(e.target.value)}
      />
    )}
