import React, { useState } from "react";
import i18n from "./i18n";
import axios from "axios";

const Multilingual = () => {
  const [language, setLanguage] = useState("en");
  const [emailOrMobile, setEmailOrMobile] = useState("");
  const [otp, setOtp] = useState("");
  const [isVerified, setIsVerified] = useState(false);

  const handleLanguageChange = async (lang) => {
    setLanguage(lang);
    i18n.changeLanguage(lang);

    if (lang === "fr") {
      // Email-based OTP
      const handleSendOtp = async () => {
        if (!email) {
          alert("Please enter your email.");
          return;
        }
    
        try {
          const response = await axios.post("http://localhost:5000/api/send-otp", { email });
          setOtpSent(true);
          setMessage(response.data.message || "OTP sent successfully!");
        } catch (error) {
          setMessage(error.response?.data?.message || "Failed to send OTP.");
        }
      };
    } else {
      // Mobile-based OTP
      const mobile = prompt("Enter your mobile number:");
      setEmailOrMobile(mobile);
      const response = await axios.post("http://localhost:5000/api/send-otp", {
        mobile,
        method: "mobile",
      });
      alert(response.data.message);
    }
  };

  const handleVerifyOtp = async () => {
    if (!otp) {
      setMessage("Please enter the OTP.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/verify-otp", { email, otp });
      if (response.data.success) {
        setVerified(true);
        setMessage("OTP verified! Fetching the answer...");
        fetchAnswer();
      } else {
        setMessage("Invalid or expired OTP. Please try again.");
      }
    } catch (error) {
      setMessage(error.response?.data?.message || "Failed to verify OTP. Please try again.");
    }
  };

  return (
    <div>
      <h1>{i18n.t("welcome_message")}</h1>
      <select onChange={(e) => handleLanguageChange(e.target.value)} value={language}>
        <option value="en">English</option>
        <option value="es">Spanish</option>
        <option value="hi">Hindi</option>
        <option value="pt">Portuguese</option>
        <option value="zh">Chinese</option>
        <option value="fr">French</option>
      </select>

      {emailOrMobile && (
        <div>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <button onClick={handleVerifyOtpverifyOtp}>Verify OTP</button>
        </div>
      )}
    </div>
  );
};

export default Multilingual;
