import React, { useState } from "react";
import axios from "axios";

const Multilingual = () => {
  const [language, setLanguage] = useState("en");
  const [emailOrMobile, setEmailOrMobile] = useState("");
  const [otp, setOtp] = useState("");
  const [isVerified, setIsVerified] = useState(false);
  const [message, setMessage] = useState("");

  const handleLanguageChange = async (lang) => {
    setLanguage(lang);
    if (isVerified) {
      await translatePage(lang);
    }
  };

  const sendOtp = async (lang) => {
    if (lang === "fr") {
      if (!emailOrMobile) {
        alert("Please enter your email.");
        return;
      }

      try {
        const response = await axios.post("http://localhost:5000/api/send-otp", { email: emailOrMobile });
        setMessage(response.data.message || "OTP sent successfully!");
      } catch (error) {
        setMessage(error.response?.data?.message || "Failed to send OTP.");
      }
    } else {
      const mobile = prompt("Enter your mobile number:");
      if (!mobile || mobile.trim() === "") {
        alert("Mobile number is required.");
        return;
      }

      setEmailOrMobile(mobile.trim());

      try {
        const response = await axios.post("http://localhost:5000/api/send-otp", {
          mobile,
          method: "mobile",
        });
        setMessage(response.data.message || "OTP sent successfully!");
      } catch (error) {
        setMessage(error.response?.data?.message || "Failed to send OTP.");
      }
    }
  };

  const handleVerifyOtp = async () => {
    if (!otp) {
      alert("Please enter the OTP.");
      return;
    }

    try {
      const payload =
        language === "fr"
          ? { email: emailOrMobile, otp }
          : { mobile: emailOrMobile, otp };

      const response = await axios.post("http://localhost:5000/api/verify-otp", payload);

      setIsVerified(true);
      setMessage(response.data.message || "OTP verified successfully!");
      await translatePage(language);
    } catch (error) {
      setMessage(error.response?.data?.message || "Invalid or expired OTP.");
    }
  };

  const translatePage = async (targetLanguage) => {
    const textNodes = getTextNodes(document.body);
    const textArray = textNodes.map((node) => node.textContent);

    try {
      const chunks = chunkArray(textArray, 10);
      for (const chunk of chunks) {
        const response = await axios.post(
          `https://api-inference.huggingface.co/models/Helsinki-NLP/opus-mt-en-${targetLanguage}`,
          { inputs: chunk },
          {
            headers: { Authorization: `Bearer YOUR_HUGGINGFACE_API_KEY` },
          }
        );

        if (Array.isArray(response.data) && response.data.length === chunk.length) {
          for (let i = 0; i < chunk.length; i++) {
            const index = textArray.indexOf(chunk[i]);
            textNodes[index].textContent = response.data[i];
          }
        }
      }
    } catch (error) {
      console.error("Translation Error:", error.response?.data || error.message);
    }
  };

  const getTextNodes = (node) => {
    let textNodes = [];
    const walker = document.createTreeWalker(node, NodeFilter.SHOW_TEXT, {
      acceptNode: (node) =>
        node.nodeValue.trim() ? NodeFilter.FILTER_ACCEPT : NodeFilter.FILTER_REJECT,
    });

    while (walker.nextNode()) {
      textNodes.push(walker.currentNode);
    }
    return textNodes;
  };

  return (
    <div>
      <h1>Welcome to the OTP verification system</h1>
      <select onChange={(e) => handleLanguageChange(e.target.value)} value={language}>
        <option value="en">English</option>
        <option value="es">Spanish</option>
        <option value="hi">Hindi</option>
        <option value="pt">Portuguese</option>
        <option value="zh">Chinese</option>
        <option value="fr">French</option>
      </select>

      {language === "fr" && (
        <div>
          <input
            type="email"
            placeholder="Enter your email"
            value={emailOrMobile}
            onChange={(e) => setEmailOrMobile(e.target.value)}
          />
          <button onClick={() => sendOtp("fr")}>Send OTP</button>
        </div>
      )}

      {message && <p>{message}</p>}

      {emailOrMobile && !isVerified && (
        <div>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <button onClick={handleVerifyOtp}>Verify OTP</button>
        </div>
      )}

      {isVerified && <p>Verification successful! You are authenticated.</p>}
    </div>
  );
};

export default Multilingual;
