import React, { useState, useEffect } from 'react'
import Leftsidebar from '../../Comnponent/Leftsidebar/Leftsidebar'
import { useParams } from 'react-router-dom'
import moment from 'moment'
import { useSelector } from 'react-redux'
import Avatar from '../../Comnponent/Avatar/Avatar'
import Editprofileform from './Edirprofileform'
import Profilebio from './Profilebio'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBirthdayCake, faPen } from '@fortawesome/free-solid-svg-icons'
import TransferPoints from './TransferPoints';
import { updatePoints, transferPoints } from '../../action/points';
import { getUserPoints } from '../../action/userpoints.js';


const Userprofile = ({ slidein }) => {
  const { id } = useParams()
  const [Switch, setswitch] = useState(false);
  const [showTransferModal, setShowTransferModal] = useState(false);
  const [pointsToTransfer, setPointsToTransfer] = useState(0);

  const users = useSelector((state)=>state.usersreducer)
  const currentprofile = users.filter((user) => user._id === id)[0]
  const currentuser = useSelector((state)=>state.currentuserreducer)
  const points = useSelector(state => state.pointsReducer);
  // console.log(currentuser._id)

  useEffect(() => {
    if (currentuser?.result._id) {
      dispatch(getUserPoints(currentuser?.result._id))
    }
  }, [currentuser, dispatch]);

  const handleTransfer = () => {
    if (pointsToTransfer > 0 && currentuser._id !== id) {
      dispatch(transferPoints(currentuser._id, id, pointsToTransfer));
    }
  };



  return (
    <div className="home-container-1">
      <Leftsidebar slidein={slidein} />
      <div className="home-container-2">
        <section>
          <div className="user-details-container">
            <div className="user-details">
              <Avatar backgroundColor="purple" color="white" fontSize="50px" px="40px" py="30px">{currentprofile.name.charAt(0).toUpperCase()}</Avatar>
              <div className="user-name">
                <h1>{currentprofile?.name}</h1>
                <p>
                  <FontAwesomeIcon icon={faBirthdayCake} /> Joined{" "} {moment(currentprofile?.joinedon).fromNow()}
                </p>
                <p className="user-points">
                  <strong>Points: </strong>{points.points}
                </p>
              </div>
            </div>
            {currentuser?.result?._id === id && ( 
              <button className="edit-profile-btn" type='button' onClick={() => setswitch(true)}><FontAwesomeIcon icon={faPen} /> Edit Profile</button>
            )}
            {currentprofile.points > 10 && currentuser?.result?._id !== id && ( // Show Transfer Points button
              <button
                className="transfer-points-btn"
                type="button"
                onClick={() => setShowTransferModal(true)}
              >
                Transfer Points
              </button>
            )}
          </div>
          <>
            {Switch ? (
              <Editprofileform currentuser={currentuser} setswitch={setswitch} />
            ) : (
              <Profilebio currentprofile={currentprofile} />
            )}
          </>
        </section>
      </div>
      {showTransferModal && (
        <TransferPoints
          currentprofile={currentprofile}
          setTransferModal={setShowTransferModal}
          points={currentprofile.points}
          setPoints={(newPoints) => {
            // Update points (can be done locally or by dispatching an action to update Redux state)
          }}
        />
      )}

      </div>

  )
}

export default Userprofile
