import React, { useState } from "react";
import axios from "axios";

const TransferPoints = ({ currentUser }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [pointsToTransfer, setPointsToTransfer] = useState("");
  const [selectedUser, setSelectedUser] = useState(null);
  const [message, setMessage] = useState("");

  const handleSearch = async () => {
    try {
      const { data } = await axios.get(`/api/users/search?query=${searchQuery}`);
      setSearchResults(data);
      setMessage("");
    } catch (error) {
      setMessage("Error fetching user profiles. Please try again.");
    }
  };

  const handleTransfer = async () => {
    if (parseInt(pointsToTransfer) <= 0) {
      setMessage("Please enter a valid number of points to transfer.");
      return;
    }

    if (currentUser.points < 10) {
      setMessage("You need at least 10 points to make a transfer.");
      return;
    }

    if (parseInt(pointsToTransfer) > currentUser.points) {
      setMessage("You cannot transfer more points than you currently have.");
      return;
    }

    try {
      const response = await axios.post(`/api/users/transfer-points`, {
        senderId: currentUser._id,
        receiverId: selectedUser._id,
        points: parseInt(pointsToTransfer),
      });
      setMessage(response.data.message);
      setPointsToTransfer("");
      setSelectedUser(null);
      // Optionally update currentUser points after a successful transfer
    } catch (error) {
      setMessage(
        error.response?.data?.message || "Error transferring points. Please try again."
      );
    }
  };

  return (
    <div className="transfer-points-container">
      <h1 className="text-2xl font-bold mb-4">Transfer Points</h1>
      <p className="text-gray-700 mb-6">Your current points: {currentUser.points}</p>

      <div className="search-section mb-6">
        <input
          type="text"
          placeholder="Search for a user"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="p-2 border border-gray-300 rounded-md w-full mb-2"
        />
        <button
          onClick={handleSearch}
          className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
        >
          Search
        </button>
      </div>

      {searchResults.length > 0 && (
        <div className="search-results mb-6">
          <h2 className="text-lg font-semibold mb-2">Search Results:</h2>
          <ul className="list-disc pl-4">
            {searchResults.map((user) => (
              <li
                key={user._id}
                onClick={() => setSelectedUser(user)}
                className={`cursor-pointer ${
                  selectedUser && selectedUser._id === user._id
                    ? "bg-gray-200"
                    : ""
                } p-2 border rounded mb-2`}
              >
                {user.name} - {user.email}
              </li>
            ))}
          </ul>
        </div>
      )}

      {selectedUser && (
        <div className="transfer-section mb-6">
          <h3 className="text-lg font-semibold mb-2">
            Transfer points to {selectedUser.name}
          </h3>
          <input
            type="number"
            placeholder="Enter points to transfer"
            value={pointsToTransfer}
            onChange={(e) => setPointsToTransfer(e.target.value)}
            className="p-2 border border-gray-300 rounded-md w-full mb-2"
          />
          <button
            onClick={handleTransfer}
            className="bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600"
          >
            Transfer Points
          </button>
        </div>
      )}

      {message && <p className="text-red-500 mt-4">{message}</p>}
    </div>
  );
};

export default TransferPoints;
