import React, { useState } from 'react';
import axios from 'axios';

const TransferPoints = ({ currentprofile, setTransferModal, points, setPoints }) => {
  const [pointsToTransfer, setPointsToTransfer] = useState(0); // Points to transfer
  const [recipient, setRecipient] = useState(''); // Recipient user ID
  const [error, setError] = useState('');

  // Handle the point transfer
  const handleTransferPoints = async () => {
    if (pointsToTransfer > 0 && pointsToTransfer <= points) {
      if (recipient !== currentprofile._id) {
        try {
          const response = await axios.post('/api/transfer', {
            recipient,
            points: pointsToTransfer,
          });
          setPoints(points - pointsToTransfer); // Update points locally
          setTransferModal(false); // Close the modal
          alert('Points transferred successfully!');
        } catch (error) {
          setError('Error transferring points. Please try again.');
          console.error('Error:', error);
        }
      } else {
        setError('You cannot transfer points to yourself.');
      }
    } else {
      setError('Invalid points or insufficient balance.');
    }
  };

  return (
    <div className="transfer-modal">
      <div className="modal-content">
        <h2>Transfer Points</h2>

        {/* Recipient input */}
        <input
          type="text"
          value={recipient}
          onChange={(e) => setRecipient(e.target.value)}
          placeholder="Enter recipient ID"
        />

        {/* Points to transfer */}
        <input
          type="number"
          value={pointsToTransfer}
          onChange={(e) => setPointsToTransfer(e.target.value)}
          placeholder="Enter points to transfer"
        />

        {/* Error message */}
        {error && <p className="error-message">{error}</p>}

        <div className="modal-actions">
          <button onClick={handleTransferPoints}>Transfer</button>
          <button onClick={() => setTransferModal(false)}>Cancel</button>
        </div>
      </div>
    </div>
  );
};

export default TransferPoints;
