import i18n from "i18next";
import { initReactI18next } from "react-i18next";

const resources = {
  en: { translation: { welcome_message: "Welcome to Stack Overflow!" } },
  es: { translation: { welcome_message: "¡Bienvenido a Stack Overflow!" } },
  hi: { translation: { welcome_message: "स्टैक ओवरफ्लो में आपका स्वागत है!" } },
  pt: { translation: { welcome_message: "Bem-vindo ao Stack Overflow!" } },
  zh: { translation: { welcome_message: "欢迎来到 Stack Overflow！" } },
  fr: { translation: { welcome_message: "Bienvenue sur Stack Overflow !" } },
};

i18n.use(initReactI18next).init({
  resources,
  lng: "en",
  fallbackLng: "en",
  interpolation: { escapeValue: false },
});

export default i18n;
