import React, { useState } from 'react';
import './Askquestion.css';
import { useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { askquestion } from '../../action/question';

const Askquestion = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const user = useSelector((state) => state.currentuserreducer);

    const [questiontitle, setquestiontitle] = useState("");
    const [questionbody, setquestionbody] = useState("");
    const [questiontag, setquestiontags] = useState("");
    const [videoFile, setVideoFile] = useState(null);
    const [otp, setOtp] = useState("");
    const [isOtpVerified, setIsOtpVerified] = useState(false);
    const [email, setEmail] = useState("");


    const handleSendOtp = async () => {
        try {
          await axios.post("/api/otp/send", { email });
          alert("OTP sent to your email");
        } catch (error) {
          alert("Error sending OTP");
          console.error(error);
        }
      };

      const handleVerifyOtp = async () => {
        try {
          const { data } = await axios.post("/api/otp/verify", { email, otp });
          if (data.verified) {
            setIsOtpVerified(true);
            alert("OTP verified successfully!");
          } else {
            alert("Invalid OTP");
          }
        } catch (error) {
          alert("Error verifying OTP");
          console.error(error);
        }
      };

      const validateUploadTime = () => {
        const currentHour = new Date().getHours();
        return currentHour >= 14 && currentHour < 19; // 2 PM to 7 PM
      };

      const handleVideoUpload = (file) => {
        if (!validateUploadTime()) {
          alert("Videos can only be uploaded between 2 PM and 7 PM.");
          return false;
        }

        if (file.size > 50 * 1024 * 1024) {
          alert("File size cannot exceed 50MB.");
          return false;
        }

        const video = document.createElement("video");
        video.preload = "metadata";

        video.onloadedmetadata = () => {
          window.URL.revokeObjectURL(video.src);
          if (video.duration > 120) {
            alert("Video duration cannot exceed 2 minutes.");
            return false;
          }
        };

        video.src = URL.createObjectURL(file);
        return true;
      };



    const handlesubmit = (e) => {
        e.preventDefault();
        if (!user) {
            alert("Login to ask a question");
            return;
          }

          if (!isOtpVerified) {
            alert("Please verify OTP before submitting the question.");
            return;
          }

          if (!validateUploadTime()) {
            alert("Videos can only be uploaded between 2 PM and 7 PM.");
            return;
          }

          if (!questionbody || !questiontitle || !questiontag) {
            alert("Please fill in all fields.");
            return;
          }

          if (!videoFile) {
            alert("Please upload a video.");
            return;
          }

        if (user) {
            if (questionbody && questiontitle && questiontag) {
                const formData = new FormData();
                formData.append("questiontitle", questiontitle);
                formData.append("questionbody", questionbody);
                formData.append("questiontag", questiontag);
                formData.append("userposted", user.result.name);
                formData.append("video", videoFile);


                dispatch(askquestion(formData, navigate));
                alert("You have successfully posted a question");
            } else {
                alert("Please enter all the fields");
            }
        } else {
            alert("Login to ask a question");
        }
    };

    return (
        <div className="ask-question">
            <div className="ask-ques-container">
                <h1>Ask a Public Question</h1>
                <form onSubmit={handlesubmit}>
                    <div className="ask-form-container">
                        <label htmlFor="ask-ques-title">
                            <h4>Title</h4>
                            <p>Be specific and imagine you're asking a question to another person</p>
                            <input
                                type="text"
                                id="ask-ques-title"
                                onChange={(e) => setquestiontitle(e.target.value)}
                                placeholder="e.g. Is there an R function for finding the index of an element in a vector?"
                            />
                        </label>
                        <label htmlFor="ask-ques-body">
                            <h4>Body</h4>
                            <p>Include all the information someone would need to answer your question</p>
                            <textarea
                                id="ask-ques-body"
                                onChange={(e) => setquestionbody(e.target.value)}
                                cols="30"
                                rows="10"
                            ></textarea>
                        </label>
                        <label htmlFor="ask-ques-tags">
                            <h4>Tags</h4>
                            <p>Add up to 5 tags to describe what your question is about</p>
                            <input
                                type="text"
                                id="ask-ques-tags"
                                onChange={(e) => setquestiontags(e.target.value.split(" "))}
                                placeholder="e.g. (xml typescript wordpress)"
                            />
                        </label>
                        <label htmlFor="email">
                            <h4>Email</h4>
                            <input
                                type="email"
                                id="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                placeholder="Enter your email"
                            />
                            <button type="button" onClick={handleSendOtp}>
                                Send OTP
                            </button>
                        </label>
                        <label htmlFor="otp">
                            <h4>OTP</h4>
                            <input
                                type="text"
                                id="otp"
                                value={otp}
                                onChange={(e) => setOtp(e.target.value)}
                                placeholder="Enter OTP"
                            />
                            <button type="button" onClick={handleVerifyOtp}>
                                Verify OTP
                            </button>
                        </label>
                        <label htmlFor="ask-ques-video">
                            <h4>Video (optional)</h4>
                            <p>Upload a video to give more context to your question</p>
                            <input
                                type="file"
                                id="ask-ques-video"
                                accept="video/*"
                                onChange={(e) => setVideoFile(e.target.files[0])}
                            />
                        </label>
                    </div>
                    <input type="submit" value="Review your question" className="review-btn" />
                </form>
            </div>
        </div>
    );
};

export default Askquestion;
