import { UPDATE_POINTS, TRANSFER_POINTS, ERROR } from '../action/points,js';

const initialState = {
  points: 0,
  error: null,
};

const pointsReducer = (state = initialState, action) => {
  switch (action.type) {
    case UPDATE_POINTS:
      return {
        ...state,
        points: action.payload.points, // Update the points in the store
      };
    case TRANSFER_POINTS:
      return {
        ...state,
        points: action.payload.points, // Update the points after transfer
      };
    case ERROR:
      return {
        ...state,
        error: action.payload, // Store error message in state
      };
    default:
      return state;
  }
};

export default pointsReducer;
