import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Auth.css";
import { resetpassword } from "../../api/index";
import { useTranslation } from 'react-i18next';


const ResetPassword = () => {
    const [emailOrPhone, setEmailOrPhone] = useState("");
    const [otp, setOtp] = useState("");
    const [step, setStep] = useState(1);
    const [message, setMessage] = useState("");
    const navigate = useNavigate();
    const { t } = useTranslation();

    const handleRequestOtp = async (e) => {
        e.preventDefault();
        setMessage("");
        try {
            const response = await axios.post("http://localhost:5000/api/send-otp", { email });
            setMessage(response.data.message);
            setStep(2); 
        } catch (error) {
            setMessage(error.response?.data?.message || "Something went wrong");
        }
    };

    const handleVerifyOtp = async (e) => {
        e.preventDefault();
        setMessage("");
        try {
            const response = await axios.post("http://localhost:5000/api/verify-otp", { email, otp });
            setMessage(response.data.message);
            if (response.data.success) {
                alert("A new password has been sent to your email.");
                navigate("/login"); // Redirect to login page after password reset
            }
        } catch (error) {
            setMessage(error.response?.data?.message || "Invalid OTP, try again");
        }
    };

    const handleResetPassword = async (e) => {
        e.preventDefault();
        try {
            const response = await resetpassword(emailOrPhone);
            setMessage(response.data.message);
        } catch (error) {
            setMessage(error.response?.data?.message || "Something went wrong");
        }
    };

    return (
        <section className="auth-section">
            <div className="auth-container-2">
                <h1>{t('Reset Password')}</h1>
                {step === 1 ? (
                    <form onSubmit={handleRequestOtp}>
                        <label htmlFor="email">
                            <h4>{t("Enter Email")}</h4>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={email}
                                onChange={(e) => setEmailOrPhone(e.target.value)}
                                required
                            />
                        </label>
                        <button type="submit" className="auth-btn">
                            {t("Send OTP")}
                        </button>
                    </form>
                ) : (
                    <form onSubmit={handleVerifyOtp}>
                        <label htmlFor="otp">
                            <h4>{t("Enter OTP")}</h4>
                            <input
                                type="text"
                                id="otp"
                                name="otp"
                                value={otp}
                                onChange={(e) => setOtp(e.target.value)}
                                required
                            />
                        </label>
                        <button type="submit" className="auth-btn">
                            {t("Verify OTP & Reset Password")}
                        </button>
                    </form>
                )}
                {/* <form onSubmit={handleResetPassword}>
                    <label htmlFor="emailOrPhone">
                        <h4>{t('Email or Phone')}</h4>
                        <input
                            type="text"
                            id="emailOrPhone"
                            name="emailOrPhone"
                            value={emailOrPhone}
                            onChange={(e) => setEmailOrPhone(e.target.value)}
                        />
                    </label>
                    <button type="submit" className="auth-btn">
                        {t('Reset Password')}
                    </button>
                </form> */}
                {message && <p>{t(message)}</p>}
            </div>
        </section>
    );
    };    

export default ResetPassword;
