import React, { useState } from "react";
import axios from "axios";

const Chatbot = () => {
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [question, setQuestion] = useState("");
  const [message, setMessage] = useState("");
  const [otpSent, setOtpSent] = useState(false);
  const [verified, setVerified] = useState(false);
  const [showQuestionInput, setShowQuestionInput] = useState(false);

  const handleSendOtp = async () => {
    if (!email) {
      setMessage("Please enter your email.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/send-otp", { email });
      setMessage(response.data.message);
      setOtpSent(true);
    } catch (error) {
      setMessage(error.response?.data?.message || "Failed to send OTP. Please try again.");
    }
  };

  const handleVerifyOtp = async () => {
    if (!otp) {
      setMessage("Please enter the OTP.");
      return;
    }

    try {
      setVerified(true);
      setMessage("OTP verified! You can now ask a question.");
      setShowQuestionInput(true);
    } catch (error) {
      setMessage("Invalid OTP. Please try again.");
    }
  };

  const handleAskQuestion = async () => {
    setShowQuestionInput(false); // Reset question input for new OTP verification
    setVerified(false); // Require OTP verification for every new question
    setOtpSent(false); // Trigger a new OTP send

    if (!email) {
      setMessage("Please enter your email.");
      return;
    }

    // Trigger OTP sending again
    try {
      const response = await axios.post("http://localhost:5000/api/send-otp", { email });
      setMessage(response.data.message);
      setOtpSent(true); // Ask the user to verify the OTP before proceeding
    } catch (error) {
      setMessage(error.response?.data?.message || "Failed to send OTP. Please try again.");
    }
  };

  const handleSubmitQuestion = async () => {
    if (!verified) {
      setMessage("Please verify OTP before asking a question.");
      return;
    }

    if (!question) {
      setMessage("Please enter a question.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/chatbot", {
        email,
        otp,
        question,
      });
      setMessage(response.data.message);
    } catch (error) {
      setMessage(error.response?.data?.message || "Error occurred. Please try again.");
    }
  };

  return (
    <div className="chatbot-container">
      <h2 className="chatbot-header">Chatbot with OTP Verification</h2>

      {!otpSent ? (
        <>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="chatbot-input"
          />
          <button onClick={handleSendOtp} className="chatbot-button">
            Send OTP
          </button>
        </>
      ) : !verified ? (
        <>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            className="chatbot-input"
          />
          <button onClick={handleVerifyOtp} className="chatbot-button">
            Verify OTP
          </button>
        </>
      ) : showQuestionInput ? (
        <>
          <textarea
            placeholder="Ask your programming question..."
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            className="chatbot-textarea"
          />
          <button onClick={handleSubmitQuestion} className="chatbot-button">
            Submit Question
          </button>
        </>
      ) : (
        <button onClick={handleAskQuestion} className="chatbot-button">
          Ask New Question
        </button>
      )}

      {message && <p className="chatbot-message">{message}</p>}
    </div>
  );
};

export default Chatbot;
