import React, { useState } from "react";
import axios from "axios";

const Chatbot = () => {
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [question, setQuestion] = useState("");
  const [loading, setLoading] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [chatbotAnswer, setChatbotAnswer] = useState("");
  const [error, setError] = useState("");

  const handleGenerateOtp = async () => {
    setLoading(true);
    setError("");
    try {
      await axios.post("/api/generate-otp", { email }); // Simulated API call
      setOtpSent(true);
    } catch (err) {
      setError("Failed to send OTP.");
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async () => {
    setLoading(true);
    setError("");
    try {
      const response = await axios.post("/api/chatbot", { email, otp, question });
      setChatbotAnswer(response.data.answer);
    } catch (err) {
      setError("Failed to get chatbot response.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Chatbot</h2>
      <div>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" />
        <button onClick={handleGenerateOtp} disabled={loading}>
          {otpSent ? "OTP Sent" : "Generate OTP"}
        </button>
      </div>
      {otpSent && (
        <div>
          <input type="text" value={otp} onChange={(e) => setOtp(e.target.value)} placeholder="OTP" />
        </div>
      )}
      {otpSent && (
        <div>
          <input type="text" value={question} onChange={(e) => setQuestion(e.target.value)} placeholder="Ask a question" />
          <button onClick={handleSubmit} disabled={loading}>Ask</button>
        </div>
      )}
      {loading && <p>Loading...</p>}
      {chatbotAnswer && <p>Answer: {chatbotAnswer}</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
};

export default Chatbot;
