import React, { useState, useEffect } from 'react';
import Leftsidebar from '../../Comnponent/Leftsidebar/Leftsidebar';
import { useParams } from 'react-router-dom';
import moment from 'moment';
import { useSelector } from 'react-redux';
import Avatar from '../../Comnponent/Avatar/Avatar';
import Editprofileform from './Edirprofileform';
import Profilebio from './Profilebio';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBirthdayCake, faPen, faCoins } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

const Userprofile = ({ slidein }) => {
  const { id } = useParams();
  const [Switch, setswitch] = useState(false);
  const [points, setPoints] = useState(0);  // State to hold points data
  const [transferModal, setTransferModal] = useState(false);  // State to show transfer modal
  const [pointsToTransfer, setPointsToTransfer] = useState(0); // Points to transfer

  const users = useSelector((state) => state.usersreducer);
  const currentprofile = users.filter((user) => user._id === id)[0];
  const currentuser = useSelector((state) => state.currentuserreducer);

  useEffect(() => {
    if (currentprofile) {
      setPoints(currentprofile.points || 0); // Fetch points from profile
    }
  }, [currentprofile]);

  const handleTransferPoints = async () => {
    if (pointsToTransfer > 0 && pointsToTransfer <= points) {
      try {
        const response = await axios.post('/api/transfer', {
          recipient: currentprofile._id,
          points: pointsToTransfer,
        });
        setPoints(points - pointsToTransfer); // Update points locally
        setTransferModal(false); // Close transfer modal
        alert('Points transferred successfully!');
      } catch (error) {
        console.error('Error transferring points:', error);
      }
    } else {
      alert('Insufficient points or invalid amount.');
    }
  };

  return (
    <div className="home-container-1">
      <Leftsidebar slidein={slidein} />
      <div className="home-container-2">
        <section>
          <div className="user-details-container">
            <div className="user-details">
              <Avatar backgroundColor="purple" color="white" fontSize="50px" px="40px" py="30px">
                {currentprofile.name.charAt(0).toUpperCase()}
              </Avatar>
              <div className="user-name">
                <h1>{currentprofile?.name}</h1>
                <p>
                  <FontAwesomeIcon icon={faBirthdayCake} /> Joined {moment(currentprofile?.joinedon).fromNow()}
                </p>
              </div>
            </div>

            {/* Show points */}
            <div className="user-points">
              <FontAwesomeIcon icon={faCoins} />
              <span>{points} Points</span>
            </div>

            {/* Transfer Points Button */}
            {currentuser?.result?._id === id && (
              <button className="transfer-points-btn" type="button" onClick={() => setTransferModal(true)}>
                Transfer Points
              </button>
            )}

            {/* Edit Profile Button */}
            {currentuser?.result?._id === id && (
              <button className="edit-profile-btn" type="button" onClick={() => setswitch(true)}>
                <FontAwesomeIcon icon={faPen} /> Edit Profile
              </button>
            )}
          </div>

          {/* Edit Profile or Bio */}
          <>
            {Switch ? (
              <Editprofileform currentuser={currentuser} setswitch={setswitch} />
            ) : (
              <Profilebio currentprofile={currentprofile} />
            )}
          </>

          {/* Transfer Points Modal */}
          {transferModal && (
            <div className="transfer-modal">
              <div className="modal-content">
                <h2>Transfer Points</h2>
                <input
                  type="number"
                  value={pointsToTransfer}
                  onChange={(e) => setPointsToTransfer(e.target.value)}
                  placeholder="Enter points to transfer"
                />
                <div className="modal-actions">
                  <button onClick={handleTransferPoints}>Transfer</button>
                  <button onClick={() => setTransferModal(false)}>Cancel</button>
                </div>
              </div>
            </div>
          )}
        </section>
      </div>
    </div>
  );
};

export default Userprofile;
