import React, { useState } from "react";
import "./Askquestion.css";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { askquestion } from "../../action/question";
import axios from "axios";

const Askquestion = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const user = useSelector((state) => state.currentuserreducer);
  const [questiontitle, setquestiontitle] = useState("");
  const [questionbody, setquestionbody] = useState("");
  const [questiontag, setquestiontags] = useState("");
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [videoFile, setVideoFile] = useState(null);
  const [message, setMessage] = useState("");

  const handleSendOtp = async () => {
    if (!email) {
      alert("Please enter your email.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/send-otp", { email });
      setOtpSent(true);
      setMessage(response.data.message || "OTP sent successfully!");
    } catch (error) {
      setMessage(error.response?.data?.message || "Failed to send OTP.");
    }
  };

  const handleVerifyOtp = async () => {
    if (!otp) {
      alert("Please enter the OTP.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/verify-otp", { email, otp });
      setOtpVerified(true);
      setMessage(response.data.message || "OTP verified successfully!");
    } catch (error) {
      setMessage(error.response?.data?.message || "Invalid or expired OTP.");
    }
  };

  const handleVideoUpload = async (videoFile) => {
    const formData = new FormData();
    formData.append("video", videoFile);
    formData.append("email", email);
    formData.append("otp", otp);

    try {
      const response = await axios.post("http://localhost:5000/api/upload-video", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      return response.data.videoPath; // Return the video path from the server response
    } catch (error) {
      console.error(error);
      throw new Error(error.response?.data?.message || "Failed to upload video.");
    }
  };

  const handlesubmit = async (e) => {
    e.preventDefault();

    if (!user) {
      alert("Login to ask a question");
      return;
    }

    if (!questionbody || !questiontitle || !questiontag) {
      alert("Please enter all the fields");
      return;
    }

    let videoPath = null;

    // Handle video upload if a file is selected
    if (videoFile) {
      try {
        videoPath = await handleVideoUpload(videoFile); // Upload the video and get the video path
      } catch (error) {
        alert(error.message);
        return;
      }
    }

    // Dispatch the askquestion action with the video path
    dispatch(
      askquestion(
        {
          questiontitle,
          questionbody,
          questiontag,
          userposted: user.result.name,
          video: videoPath, // Include video path in the question payload
        },
        navigate
      )
    );

    alert("You have successfully posted a question");
  };


  const handleenter = (e) => {
    if (e.code === "Enter") {
      setquestionbody(questionbody + "\n");
    }
  };

  return (
    <div className="ask-question">
      <div className="ask-ques-container">
        <h1>Ask a public Question</h1>
        <form onSubmit={handlesubmit}>
          <div className="ask-form-container">
            <label htmlFor="ask-ques-title">
              <h4>Title</h4>
              <p>
                Be specific and imagine you're asking a question to another
                person
              </p>
              <input
                type="text"
                id="ask-ques-title"
                onChange={(e) => {
                  setquestiontitle(e.target.value);
                }}
                placeholder="e.g. Is there an R function for finding the index of an element in a vector?"
              />
            </label>
            <label htmlFor="ask-ques-body">
              <h4>Body</h4>
              <p>
                Include all the information someone would need to answer your
                question
              </p>
              <textarea
                name=""
                id="ask-ques-body"
                onChange={(e) => {
                  setquestionbody(e.target.value);
                }}
                cols="30"
                rows="10"
                onKeyDown={handleenter}
              ></textarea>
            </label>
            <label htmlFor="ask-ques-tags">
              <h4>Tags</h4>
              <p>Add up to 5 tags to describe what your question is about</p>
              <input
                type="text"
                id="ask-ques-tags"
                onChange={(e) => {
                  setquestiontags(e.target.value.split(" "));
                }}
                placeholder="e.g. (xml typescript wordpress)"
              />
            </label>
          </div>

          <div className="otp-section">
            <label htmlFor="email">
              <h4>Email</h4>
              <p>Enter your email to receive an OTP</p>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
              />
            </label>
            <button
              type="button"
              onClick={handleSendOtp}
              disabled={otpSent}
              className="otp-btn"
            >
              Send OTP
            </button>
            {otpSent && (
              <>
                <label htmlFor="otp">
                  <h4>OTP</h4>
                  <p>Enter the OTP sent to your email</p>
                  <input
                    type="text"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    placeholder="Enter OTP"
                  />
                </label>
                <button
                  type="button"
                  onClick={handleVerifyOtp}
                  disabled={otpVerified}
                  className="otp-btn"
                >
                  Verify OTP
                </button>
              </>
            )}
          </div>

          {otpVerified && (
            <div className="video-upload-section">
              <label htmlFor="video-upload">
                <h4>Upload Video</h4>
                <p>Upload a video file (Max: 50MB)</p>
                <input
                  type="file"
                  accept="video/*"
                  onChange={handleVideoUpload}
                />
              </label>
            </div>
          )}

          <input
            type="submit"
            value="Review your question"
            className="review-btn"
          />
        </form>
      </div>
    </div>
  );
};

export default Askquestion;
