import React, { useState } from "react";
import axios from "axios";

const Multilingual = () => {
  const [language, setLanguage] = useState("en");
  const [emailOrMobile, setEmailOrMobile] = useState("");
  const [otp, setOtp] = useState("");
  const [isVerified, setIsVerified] = useState(false);
  const [message, setMessage] = useState("");

  // Handle OTP Sending based on language
  const handleSendOtp = async () => {
    if (!emailOrMobile) {
      alert(language === "fr" ? "Veuillez entrer votre e-mail." : "Please enter your mobile number.");
      return;
    }

    try {
      let response;
      if (language === "fr") {
        // Send OTP to Email
        response = await axios.post("http://localhost:5000/api/send-otp", {
          email: emailOrMobile,
        });
      } else {
        // Send OTP to Mobile (via Twilio)
        response = await axios.post("http://localhost:5000/user/send-otp-mobile", {
          mobile: emailOrMobile,
        });
      }

      setMessage(response.data.message || "OTP sent successfully!");
    } catch (error) {
      setMessage(error.response?.data?.message || "Failed to send OTP.");
    }
  };

  // Handle OTP Verification
  const handleVerifyOtp = async () => {
    if (!otp) {
      alert("Please enter the OTP.");
      return;
    }

    try {
      let response;
      if (language === "fr") {
        // Verify OTP for Email
        response = await axios.post("http://localhost:5000/api/verify-otp", {
          email: emailOrMobile,
          otp,
        });
      } else {
        // Verify OTP for Mobile
        response = await axios.post("http://localhost:5000/user/verify-otp-mobile", {
          mobile: emailOrMobile,
          otp,
        });
      }

      setIsVerified(true);
      setMessage(response.data.message || "OTP verified successfully!");
    } catch (error) {
      setMessage(error.response?.data?.message || "Invalid or expired OTP.");
    }
  };

  return (
    <div>
      <h1>OTP Verification System</h1>
      
      {/* Language Selection Dropdown */}
      <label>Select Language: </label>
      <select onChange={(e) => setLanguage(e.target.value)} value={language}>
        <option value="en">English</option>
        <option value="es">Spanish</option>
        <option value="hi">Hindi</option>
        <option value="pt">Portuguese</option>
        <option value="zh">Chinese</option>
        <option value="fr">French</option>
      </select>

      {/* Input Field for Email (French) or Mobile (Other Languages) */}
      <div>
        {language === "fr" ? (
          <input
            type="email"
            placeholder="Enter your email"
            value={emailOrMobile}
            onChange={(e) => setEmailOrMobile(e.target.value)}
          />
        ) : (
          <input
            type="text"
            placeholder="Enter your mobile number (with code)"
            value={emailOrMobile}
            onChange={(e) => setEmailOrMobile(e.target.value)}
          />
        )}
        <button onClick={handleSendOtp}>Send OTP</button>
      </div>

      {/* Message Display */}
      {message && <p>{message}</p>}

      {/* OTP Input & Verification Button */}
      {emailOrMobile && !isVerified && (
        <div>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <button onClick={handleVerifyOtp}>Verify OTP</button>
        </div>
      )}

      {/* Success Message */}
      {isVerified && <p>Verification successful! You are authenticated.</p>}
    </div>
  );
};

export default Multilingual;
