import React, { useState } from "react";
import axios from "axios";

const Multilingual = () => {
  const [language, setLanguage] = useState("en");
  const [emailOrMobile, setEmailOrMobile] = useState("");
  const [otp, setOtp] = useState("");
  const [isVerified, setIsVerified] = useState(false);
  const [message, setMessage] = useState("");

  // Handle language change and reset fields
  const handleLanguageChange = (lang) => {
    setLanguage(lang);
    setEmailOrMobile("");
    setOtp("");
    setIsVerified(false);
    setMessage("");
  };

  // Send OTP based on language selection
  const handleSendOtp = async () => {
    if (language === "fr") {
      // Send OTP via email for French users
      if (!emailOrMobile) {
        alert("Veuillez entrer votre e-mail."); // French alert message
        return;
      }

      try {
        const response = await axios.post("http://localhost:5000/api/send-otp", { email: emailOrMobile });
        setMessage(response.data.message || "OTP envoyé avec succès !");
      } catch (error) {
        setMessage(error.response?.data?.message || "Échec de l'envoi de l'OTP.");
      }
    } else {
      // Send OTP via mobile for other languages using Twilio
      const mobile = prompt("Enter your mobile number (with country code):");
      if (!mobile) {
        alert("Mobile number is required.");
        return;
      }

      setEmailOrMobile(mobile);

      try {
        const response = await axios.post("http://localhost:5000/user/send-otp-mobile", { mobile });
        setMessage(response.data.message || "OTP sent successfully via Twilio!");
      } catch (error) {
        setMessage(error.response?.data?.message || "Failed to send OTP via Twilio.");
      }
    }
  };

  // Verify OTP
  const handleVerifyOtp = async () => {
    if (!otp) {
      alert("Please enter the OTP.");
      return;
    }

    try {
      let response;
      if (language === "fr") {
        // Verify OTP via email for French users
        response = await axios.post("http://localhost:5000/api/verify-otp", {
          email: emailOrMobile,
          otp,
        });
      } else {
        // Verify OTP via mobile for other languages using Twilio
        response = await axios.post("http://localhost:5000/user/verify-otp-mobile", {
          mobile: emailOrMobile,
          otp,
        });
      }

      setIsVerified(true);
      setMessage(response.data.message || "OTP verified successfully!");
    } catch (error) {
      setMessage(error.response?.data?.message || "Invalid or expired OTP.");
    }
  };

  return (
    <div>
      <h1>Welcome to the OTP verification system</h1>

      {/* Language selection dropdown */}
      <select onChange={(e) => handleLanguageChange(e.target.value)} value={language}>
        <option value="en">English</option>
        <option value="es">Spanish</option>
        <option value="hi">Hindi</option>
        <option value="pt">Portuguese</option>
        <option value="zh">Chinese</option>
        <option value="fr">French</option>
      </select>

      {/* Input field for French (email-based OTP) */}
      {language === "fr" ? (
        <div>
          <input
            type="email"
            placeholder="Enter your email"
            value={emailOrMobile}
            onChange={(e) => setEmailOrMobile(e.target.value)}
          />
        </div>
      ) : (
        <p>OTP will be sent to your mobile number.</p>
      )}

      <button onClick={handleSendOtp}>Send OTP</button>

      {/* Display messages */}
      {message && <p>{message}</p>}

      {/* OTP Verification */}
      {emailOrMobile && !isVerified && (
        <div>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <button onClick={handleVerifyOtp}>Verify OTP</button>
        </div>
      )}

      {/* Success message after verification */}
      {isVerified && <p>Verification successful! You are authenticated.</p>}
    </div>
  );
};

export default Multilingual;
