import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Auth.css";
import { resetpassword } from "../../api";

const ResetPassword = () => {
    const [emailOrPhone, setEmailOrPhone] = useState("");
    const [message, setMessage] = useState("");
    const navigate = useNavigate();

    const handleResetPassword = async (e) => {
        e.preventDefault();
        try {
            // Send only the emailOrPhone to the backend
            const response = await axios.post("http://localhost:5000/api/auth/reset-password", { emailOrPhone });
            setMessage(response.data.message);
            setTimeout(() => navigate("/auth"), 3000); // Redirect after 3 seconds
        } catch (error) {
            setMessage(error.response?.data?.message || "Something went wrong");
        }
    };

    return (
        <section className="auth-section">
            <div className="auth-container-2">
                <h1>Reset Password</h1>
                <form onSubmit={handleResetPassword}>
                    <label htmlFor="emailOrPhone">
                        <h4>Email or Phone</h4>
                        <input
                            type="text"
                            id="emailOrPhone"
                            name="emailOrPhone"
                            value={emailOrPhone}
                            onChange={(e) => setEmailOrPhone(e.target.value)}
                        />
                    </label>
                    <button type="submit" className="auth-btn">
                        Reset Password
                    </button>
                </form>
                {message && <p>{message}</p>}
            </div>
        </section>
    );
};

export default ResetPassword;
