import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { generateOtp, getChatbotResponse } from "../../actions/chatbot";

const Chatbot = () => {
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [question, setQuestion] = useState("");

  const dispatch = useDispatch();
  const { loading, chatbotAnswer, error, otpSent } = useSelector(
    (state) => state.chatbot
  );

  const handleGenerateOtp = () => {
    dispatch(generateOtp(email));
  };

  const handleSubmit = () => {
    const data = { email, otp, question };
    dispatch(getChatbotResponse(data));
  };

  return (
    <div className="chatbot-container">
      <h2>Chatbot</h2>
      <div>
        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button onClick={handleGenerateOtp} disabled={loading}>
          {otpSent ? "OTP Sent" : "Generate OTP"}
        </button>
      </div>
      {otpSent && (
        <div>
          <label>OTP:</label>
          <input
            type="text"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
        </div>
      )}
      {otpSent && (
        <div>
          <label>Question:</label>
          <input
            type="text"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
          />
          <button onClick={handleSubmit} disabled={loading}>
            Ask Chatbot
          </button>
        </div>
      )}
      {loading && <p>Loading...</p>}
      {chatbotAnswer && <p>Answer: {chatbotAnswer}</p>}
      {error && <p>Error: {error}</p>}
    </div>
  );
};

export default Chatbot;
