import axios from 'axios';

export const UPDATE_POINTS = "UPDATE_POINTS";
export const TRANSFER_POINTS = ""

export const updatePoints = (userId, points) => async (dispatch) => {
  try {
    const response = await axios.put(`/api/users/${userId}/update-points`, { points });

    dispatch({ type: 'UPDATE_POINTS', payload: response.data });
  } catch (error) {
    console.error("Error updating points:", error);
  }
};

export const transferPoints = (fromUserId, toUserId, points) => async (dispatch) => {
  try {
    const response = await axios.post('/api/users/transfer-points', { fromUserId, toUserId, points });

    dispatch({ type: 'TRANSFER_POINTS', payload: response.data });
  } catch (error) {
    console.error("Error transferring points:", error);
  }
};
