import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { generateOtp, getChatbotResponse } from "../../action/chatbot";

const Chatbot = () => {
  const [otp, setOtp] = useState("");
  const [question, setQuestion] = useState("");

  // Access the logged-in user's email from global state
  const { email } = useSelector((state) => state.user); // Assuming email is in `state.user`

  const dispatch = useDispatch();
  const { loading, chatbotAnswer, error, otpSent } = useSelector(
    (state) => state.chatbot
  );

  const handleGenerateOtp = () => {
    dispatch(generateOtp(email)); // Send OTP to logged-in user's email
  };

  const handleSubmit = () => {
    const data = { email, otp, question }; // Email is fetched from the logged-in user's state
    dispatch(getChatbotResponse(data));
  };

  return (
    <div className="chatbot-container">
      <h2>Chatbot</h2>
      <div>
        <p>Email: <strong>{email}</strong></p> {/* Display logged-in user's email */}
        <button onClick={handleGenerateOtp} disabled={loading}>
          {otpSent ? "OTP Sent" : "Generate OTP"}
        </button>
      </div>
      {otpSent && (
        <div>
          <label>OTP:</label>
          <input
            type="text"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
        </div>
      )}
      {otpSent && (
        <div>
          <label>Question:</label>
          <input
            type="text"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
          />
          <button onClick={handleSubmit} disabled={loading}>
            Ask Chatbot
          </button>
        </div>
      )}
      {loading && <p>Loading...</p>}
      {chatbotAnswer && <p>Answer: {chatbotAnswer}</p>}
      {error && <p>Error: {error}</p>}
    </div>
  );
};

export default Chatbot;
