import React, { useState } from "react";
import axios from "axios";
import './Multilingual.css';

const Multilingual = () => {
  const [language, setLanguage] = useState("en");
  const [emailOrMobile, setEmailOrMobile] = useState("");
  const [otp, setOtp] = useState("");
  const [isVerified, setIsVerified] = useState(false);
  const [message, setMessage] = useState("");
  const [translatedText, setTranslatedText] = useState(null);


  const translatePage = async (lang) => {
    setTranslatedText("Translating... Please wait ‚è≥");

    try {
        console.log(`Requesting translation for: ${lang}`);

        // Get all text nodes dynamically
        const elements = document.body.getElementsByTagName("*");
        let textNodes = [];

        for (let i = 0; i < elements.length; i++) {
            let element = elements[i];
            for (let node of element.childNodes) {
                if (node.nodeType === Node.TEXT_NODE && node.nodeValue.trim() !== "") {
                    textNodes.push(node);
                }
            }
        }

        // Collect all texts
        let texts = textNodes.map((node) => node.nodeValue.trim());

        // Send texts to translation API
        const response = await axios.post("http://localhost:5000/api/translate", {
            text: texts,
            targetLang: lang,
        });

        console.log("Full Translation Response:", response.data);

        // Ensure the response is an array
        if (Array.isArray(response.data) && response.data.length === textNodes.length) {
            // Extract translated texts
            textNodes.forEach((node, index) => {
                node.nodeValue = response.data[index]?.translation_text || node.nodeValue;
            });

            setTranslatedText("Translation successful ‚úÖ");
        } else {
            console.error("Unexpected response format:", response.data);
            setTranslatedText("Translation failed. Unexpected response.");
        }
    } catch (error) {
        console.error("Translation Error:", error.response?.data || error.message);
        setTranslatedText("Translation failed. Please try again.");
    }
};













  /** üîπ Handle Language Selection */
  const handleLanguageChange = async (lang) => {
    setLanguage(lang);
    setTranslatedText(null); // Reset translation

    if (isVerified) {
      await translatePage(lang);
    }
  };

  /** üîπ Handle OTP Request */
  const handleSendOtp = async () => {
    if (!emailOrMobile) {
      alert(language === "fr" ? "Veuillez entrer votre email." : "Please enter an email or mobile number.");
      return;
    }

    const endpoint = language === "fr" ? "/api/send-otp" : "/user/send-otp-mobile";
    const payload = language === "fr" ? { email: emailOrMobile } : { mobile: emailOrMobile };

    try {
      const response = await axios.post(`http://localhost:5000${endpoint}`, payload);
      setMessage(response.data.message);
    } catch (error) {
      setMessage(error.response?.data?.message || "Failed to send OTP.");
    }
  };

  /** üîπ Handle OTP Verification */
  const handleVerifyOtp = async () => {
    if (!otp) {
      alert(language === "fr" ? "Veuillez entrer le code OTP." : "Please enter the OTP.");
      return;
    }

    const endpoint = language === "fr" ? "/api/verify-otp" : "/user/verify-otp-mobile";
    const payload = language === "fr" ? { email: emailOrMobile, otp } : { mobile: emailOrMobile, otp };

    try {
      const response = await axios.post(`http://localhost:5000${endpoint}`, payload);
      setIsVerified(true);
      setMessage(response.data.message || "OTP verified successfully!");
      await translatePage(language); // Translate page after OTP verification
    } catch (error) {
      setMessage(error.response?.data?.message || "Invalid or expired OTP.");
    }
  };

  return (
    <div className="otp-page">
  <h1 className="otp-title">{translatedText || "Welcome to the OTP verification system"}</h1>

  {/* üîπ Language Selection */}
  <select
    className="otp-language-selector"
    onChange={(e) => handleLanguageChange(e.target.value)}
    value={language}
  >
    <option value="en">English</option>
    <option value="es">Spanish</option>
    <option value="hi">Hindi</option>
    <option value="pt">Portuguese</option>
    <option value="zh">Chinese</option>
    <option value="fr">French</option>
  </select>

  {/* üîπ Conditional Input (Email for French, Mobile for Others) */}
  <div className="otp-input-container">
    {language === "fr" ? (
      <input
        className="otp-input"
        type="email"
        placeholder="Enter your email"
        value={emailOrMobile}
        onChange={(e) => setEmailOrMobile(e.target.value)}
      />
    ) : (
      <input
        className="otp-input"
        type="text"
        placeholder="Enter your mobile number"
        value={emailOrMobile}
        onChange={(e) => setEmailOrMobile(e.target.value)}
      />
    )}
    <button className="otp-button" onClick={handleSendOtp}>
      Send OTP
    </button>
  </div>

  {message && <p className={`otp-message ${isVerified ? "success" : "error"}`}>{message}</p>}

  {/* üîπ OTP Input Field */}
  {emailOrMobile && !isVerified && (
    <div className="otp-verification-container">
      <input
        className="otp-input"
        type="text"
        placeholder="Enter OTP"
        value={otp}
        onChange={(e) => setOtp(e.target.value)}
      />
      <button className="otp-button" onClick={handleVerifyOtp}>
        Verify OTP
      </button>
    </div>
  )}

  {/* üîπ Verification Success Message */}
  {isVerified && (
    <p className="otp-success-message">
      {translatedText || "Verification successful! You are authenticated."}
    </p>
  )}
</div>

  );
};

export default Multilingual;
